    # --- DISPLAY LOGIC ---
    def show_brand_table(title, data_dict):
        # Create balanced DataFrame
        try:
            df = pd.DataFrame(data_dict)
            if search:
                mask = df.astype(str).apply(lambda x: x.str.contains(search, case=False)).any(axis=1)
                filt = df[mask]
            else:
                filt = df
            
            if not filt.empty:
                st.header(f"üè∑Ô∏è {title}")
                st.dataframe(
                    filt, 
                    use_container_width=True, 
                    hide_index=True,
                    column_config={
                        "Consumer Price": st.column_config.NumberColumn("Consumer Price", format="‚Çπ%d"),
                        "MRP": st.column_config.NumberColumn("MRP", format="‚Çπ%d"),
                    }
                )
                st.markdown("---")
        except ValueError as e:
            st.error(f"Error in {title}: Column length mismatch. Please contact support.")

