    def show_table(title, data):
        df = pd.DataFrame(data)
        if search:
            mask = df.astype(str).apply(lambda x: x.str.contains(search, case=False)).any(axis=1)
            filt = df[mask]
        else:
            filt = df
        if not filt.empty:
            st.header(f"üè∑Ô∏è {title}")
            st.dataframe(filt, use_container_width=True, hide_index=True, column_config={"Consumer Price": st.column_config.NumberColumn("Consumer Price", format="‚Çπ%d"), "MRP": st.column_config.NumberColumn("MRP", format="‚Çπ%d")})
            st.markdown("---")

